parameters.jsCode:
const runData = $input.first().json.data.resultData.runData;

function extractErrors(runData) {
  const errors = [];

  for (const nodeName in runData) {
    for (const exec of runData[nodeName]) {
      if (exec.executionStatus === "error") {
        const description =
          exec.error?.description ||
          exec.error?.message ||
          exec.message ||
          "Unknown error";

        errors.push({ 
          nodeName, 
          description, 
          stack: exec.error.stack,
          messages: exec.error.messages,
          http: exec.error.httpCode,
          notionUrl: $input.first().json.data.resultData.runData['Get Initial Article'][0].data.main[0][0].json.body.data.url
        });
      }
    }
  }

  return errors;
}

return extractErrors(runData);


return errors;

