const subject = $('Create Topical Signature').first().json.output.topic_subject;
const action = $('Create Topical Signature').first().json.output.topic_action;
const entities = $('Create Topical Signature').first().json.output.topic_entities || [];

const orFilters = [];

// subject contains
if (subject) {
  orFilters.push({
    property: "Topic Subject",
    rich_text: { contains: subject }
  });
}

// action contains
if (action) {
  orFilters.push({
    property: "Topic Action",
    multi_select: { contains: action }
  });
}

// entity matches (create one filter per entity)
for (const e of entities) {
  orFilters.push({
    property: "Topic Entities",
    multi_select: { contains: e }
  });
}

return [
  {
    json: {
      notionFilter: {
        filter: {
          or: orFilters
        }
      }
    }
  }
];

// The Notion API response is inside the current item under $json.
// It contains: { object: "list", results: [ ...pages... ] }

const list = $input.first().json;

// Safety check:
if (!list.results || !Array.isArray(list.results)) {
  return [{ json: { error: "No Notion results found" }}];
}

const extracted = list.results.map(page => {
  const props = page.properties || {};

  // Helper to unwrap Notion rich_text → plain text
  const getRichText = (prop) => {
    try {
      const arr = props[prop]?.rich_text;
      if (arr && arr.length > 0) {
        return arr.map(t => t.plain_text || "").join(" ").trim();
      }
      return "";
    } catch {
      return "";
    }
  };

  // topic fields
  const topic_subject = getRichText("Topic Subject");
  const topic_action = getRichText("Topic Action");
  const topic_entities_raw = getRichText("Topic Entities");
  const topic_geo = getRichText("Topic Geo");
  const topic_signature = getRichText("Topic Signature");

  // Convert "a, b, c" → ["a", "b", "c"]
  const topic_entities = topic_entities_raw
    ? topic_entities_raw.split(",").map(e => e.trim()).filter(e => e.length > 0)
    : [];

  // fact fingerprint (stored as JSON string inside rich_text)
  let fact_fingerprint_raw = getRichText("Fact Fingerprint");
  let fact_fingerprint = {};
  try {
    fact_fingerprint = JSON.parse(fact_fingerprint_raw);
  } catch {
    fact_fingerprint = { parse_error: true, raw: fact_fingerprint_raw };
  }

  return {
    page_id: page.id,
    url: page.url,
    topic_subject,
    topic_action,
    topic_entities,
    topic_geo,
    topic_signature,
    fact_fingerprint
  };
});

return extracted.map(item => ({ json: item }));

