console.log("$input: ", $input)

if ($input.all().map(i => i.json.output.classification) === undefined) {
  return [{ json: { final_classification: "new" } }];
}

const results = $input.all().map(i => i.json.output.classification);

if (results.includes("update")) {
  return [{ json: { final_classification: "update" } }];
}

const allDuplicates = results.every(r => r === "duplicate");

if (allDuplicates) {
  return [{ json: { final_classification: "duplicate" } }];
}

return [{ json: { final_classification: "new" } }];

