// Cookies from initial request
const initialCookies = $('Extract Initial Cookies').first().json.cookieHeader

// Extract updateSession response headers
const updateHeaders = $input.first().json.headers || {};
const updateSetCookies = updateHeaders["set-cookie"] || [];

// Normalize to array
const updateArray = Array.isArray(updateSetCookies)
  ? updateSetCookies
  : [updateSetCookies];

// Extract only key=value from each
const updateCookies = updateArray
  .filter(Boolean)
  .map(c => c.split(";")[0]);

// Merge and de-duplicate
const mergedCookies = [
  ...initialCookies.split("; ").filter(Boolean),
  ...updateCookies
];

const deduped = [...new Set(mergedCookies)].join("; ");

return [
  {
    json: {
      cookieHeader: deduped
    }
  }
];

const headers = $input.first().json.headers

// Grab all set-cookie headers
const setCookies = headers["set-cookie"];

// Ensure it's always an array
const cookieArray = Array.isArray(setCookies) ? setCookies : [setCookies];

// Reduce to a single cookie header string
const cookieHeader = cookieArray.map(c => c.split(";")[0]).join("; ");

return [
  {
    json: {
      cookieHeader
    }
  }
];

const mergedCookies = $input.first().json.cookieHeader
const token = $('Update Session').first().json.body.sessionToken

// Build the booking URL
// Replace values with your dynamic ones if needed
const redirectURL = `https://www.marriott.com/reservation/availabilitySearch.mi?sessionToken=${token}&propertyId=santx&checkInDate=2025-12-13&checkOutDate=2025-12-17&numRooms=1&numAdults=2`;

// Build the curl
const curl = `
curl "${redirectURL}" \\
  -H "Cookie: ${mergedCookies}" \\
  -H "User-Agent: Mozilla/5.0 (Macintosh; Intel Mac OS X 10_15_7)" \\
  -H "Accept: text/html,application/xhtml+xml"
`;

return [
  {
    json: {
      redirectURL,
      curl
    }
  }
];

