    .replace(/\nThis site uses[\s\S]*$/i, '')
    // Remove social/footer sections
    .replace(/\n(Follow Us|Check Out Our|About Us|Fuel News Center|Search Site|Energy News|Grab Our Free|Proudly powered by)[\s\S]*$/i, '')
    // Remove footers (common patterns)
    .replace(/\n(Share this:|Related Posts?:|Tags?:|Categories:|Filed Under:|About the Author|Subscribe|Newsletter|Follow us)[\s\S]*$/i, '')
    // Remove navigation lists (lines starting with * followed by capitalized words)
    .replace(/(^|\n)\s*\*\s+[A-Z][^\n]*(\n\s*\*\s+[A-Z][^\n]*)*/g, '')
    // Remove "Search for:" and similar patterns
    .replace(/\n(Search for:|Submit Press Release|Free ebook|Green Job Board).*$/gm, '')
    // Remove date/author lines (e.g., "October 21, 2025 By Author Name")
    .replace(/\n[A-Z][a-z]+\s+\d{1,2},\s+\d{4}\s+By\s+[^\n]+\n/g, '\n')
    // Remove Markdown headers (e.g., #, ##, ###)
    .replace(/(^|\n)#+\s?/g, '\n')
    // Remove bold and italics (**text**, *text*, __text__, _text_)
    .replace(/(\*\*|__)(.*?)\1/g, '$2')
    .replace(/(\*|_)(.*?)\1/g, '$2')
    // Remove inline code and code blocks
    .replace(/`{1,3}[^`]*`{1,3}/g, '')
    // Remove images ![alt](url)
    .replace(/!\[.*?\]\(.*?\)/g, '')
    // Replace links [text](url) with just text
    .replace(/\[(.*?)\]\(.*?\)/g, '$1')
    // Remove blockquotes
    .replace(/(^|\n)>\s?/g, '\n')
    // Remove unordered and ordered list markers (but keep the text)
    .replace(/(^|\n)[\*\-\+]\s+/g, '\n')
    .replace(/(^|\n)\d+\.\s+/g, '\n')
    // Remove lines that are just navigation/menu items (short capitalized lines)
    .replace(/\n[A-Z][a-z\s]{2,30}\n(?=[A-Z])/g, '\n')
    // Remove escaped characters
    .replace(/\\([*_\[\](){}])/g, '$1')
    // Remove special symbols like Δ
    .replace(/Δ/g, '')
    // Remove short trailing lines (likely footer fragments)
    .replace(/\n[^\n]{1,40}$/g, '')
    // Replace multiple newlines with double newline for paragraphs
    .replace(/\n{3,}/g, '\n\n')
