={{ 
  $json.data.markdown
    // Remove HTML tags if present
    .removeTags()
    // Remove Markdown headers (e.g., #, ##, ###)
    .replace(/(^|\n)#+\s?/g, '\n')
    // Remove bold and italics (**text**, *text*, __text__, _text_)
    .replace(/(\*\*|__)(.*?)\1/g, '$2')
    .replace(/(\*|_)(.*?)\1/g, '$2')
    // Remove inline code and code blocks
    .replace(/`{1,3}[^`]*`{1,3}/g, '')
    // Remove images ![alt](url)
    .replace(/!\[.*?\]\(.*?\)/g, '')
    // Replace links [text](url) with just text
    .replace(/\[(.*?)\]\(.*?\)/g, '$1')
    // Remove blockquotes
    .replace(/(^|\n)>\s?/g, '\n')
    // Remove unordered and ordered list markers
    .replace(/(^|\n)[\*\-\+]\s+/g, '\n')
    .replace(/(^|\n)\d+\.\s+/g, '\n')
    // Replace multiple newlines with a single newline
    .replace(/\n{2,}/g, '\n\n')
    // Trim whitespace
    .trim()
}}

