{{ $json.textSubString }}

{{
  (() => {
    let text = $("Merge1").item.json.textSubString || "";
    // Remove images: ![alt](url)
    text = text.replace(/!\[.*?\]\(.*?\)/g, '');
    // Remove links but keep the text: [text](url)
    text = text.replace(/\[([^\]]+)\]\([^)]+\)/g, '$1');
    // Remove bold, italic, underline, strikethrough: **text**, *text*, __text__, _text_, ~~text~~
    text = text.replace(/(\*\*|__)(.*?)\1/g, '$2');
    text = text.replace(/(\*|_)(.*?)\1/g, '$2');
    text = text.replace(/~~(.*?)~~/g, '$1');
    // Remove inline code: `code`
    text = text.replace(/`([^`]+)`/g, '$1');
    // Remove headers: # Header
    text = text.replace(/^#+\s+(.*)/gm, '$1');
    // Remove any remaining Markdown brackets or parentheses
    text = text.replace(/[\[\]\(\)]/g, '');
    // Remove extra newlines and whitespace
    text = text.replace(/\n{2,}/g, '\n').trim();
    return text;
  })()
}}
