// Stage 1: Fossil Fuel Filter
// Returns: PASS or FAIL

const article = $json;
const articleText = (article.title + ' ' + article.content).toLowerCase();

// Target fuel keywords
const targetFuels = [
  'b100', 'b30', 'b20', 'fame', 'hvo', 'renewable diesel', 'biodiesel',
  'used cooking oil', 'uco', 'waste oil', 'en 14214', 'iscc', 'rtfo'
];

// VIP customer keywords (auto-pass)
const vipKeywords = [
  'lower thames crossing', 'balfour beatty', 'sse', 'netzero teesside', 'sizewell'
];

// Decarbonisation keywords (auto-pass Rule 3)
const decarbKeywords = [
  'decarbonisation', 'decarbonization', 'net zero', 'carbon neutral',
  'emissions reduction', 'clean energy transition'
];

// Check for any target fuel keywords
const hasBiofuel = targetFuels.some(keyword => articleText.includes(keyword));

// Check for VIP keywords
const hasVIP = vipKeywords.some(keyword => articleText.includes(keyword));

// Check for decarbonisation keywords
const hasDecarb = decarbKeywords.some(keyword => articleText.includes(keyword));

// Decision logic
if (hasBiofuel || hasVIP || hasDecarb) {
  return {
    json: {
      stage_1_decision: 'PASS',
      pass_reason: hasBiofuel ? 'biofuel_keywords' : (hasVIP ? 'vip_customer' : 'decarbonisation'),
      article: article
    }
  };
} else {
  return {
    json: {
      stage_1_decision: 'FAIL',
      article: article
    }
  };
}

// Stage 2: VIP Keyword Handler
// Returns: VIP_PASS, CONTINUE, or FAIL (if somehow Stage 1 passed incorrectly)

const article = $json.article;
const articleText = (article.title + ' ' + article.content).toLowerCase();

// VIP customer keywords (5 only)
const vipCustomers = [
  'lower thames crossing',
  'balfour beatty',
  ' sse ',
  'netzero teesside',
  'net zero teesside',
  'sizewell'
];

// Check for VIP customers
const foundVIPs = vipCustomers.filter(keyword => articleText.includes(keyword));

if (foundVIPs.length > 0) {
  return {
    json: {
      stage_2_decision: 'VIP_PASS',
      vip_customer_detected: foundVIPs[0],
      baseline_points: 2,
      skip_density_check: true,
      article: article
    }
  };
} else {
  // Not VIP, needs to go through density check
  return {
    json: {
      stage_2_decision: 'CONTINUE',
      baseline_points: 0,
      skip_density_check: false,
      article: article
    }
  };
}

