let output;

switch ($json.sentiment_score) {
  case 0:
    output = '0 - Very Negative';
    break;

  case 1:
    output = '1 - Negative';
    break;

  case 2:
    output = '2 - Relatively Negative';
    break;

  case 3:
    output = '3 - Neutral';
    break;

  case 4:
    output = '4 - Relatively Positive';
    break;

  case 5:
    output = '5 - Very Positive';
    break;

  default:
    output = '3 - Neutral';
}

return { output };

