parameters.jsCode:
// Initialize output variables
let bodyParts = [];
let images = [];

for (const item of $input.all()) {
  const block = item.json;

  if (block.type === 'paragraph') {
    const content = block.content?.trim();
    if (content) {
      bodyParts.push(content);
    }
  }

  if (block.type === 'image') {
    const imageUrl = block.image?.external?.url;
    if (imageUrl) {
      images.push(imageUrl);
    }
  }
}

return [
  {
    json: {
      body: bodyParts.join('\n'),
      images,
      initial_input: $('Set Initial Input').item.json.set_initial_input,
      system_message: $('Extract Content Type Prompt').item.json.property_system_message,
      user_message: $('Extract Content Type Prompt').item.json.property_guidelines,
      generate_images: $('Get Initial Article').first().json.body.data.properties["Generate Image"].checkbox,
      title: `Content - ${$('Extract Content Type Prompt').item.json.property_platform[0]} - ${$('Extract Content Type Prompt').item.json.property_format}`,
      source: $('Get Initial Article').item.json.body.data.id,
      platform: $('Extract Content Type Prompt').item.json.property_platform[0],
      transformed_content_db_id: $('Get Transformed Content DB ID').item.json.id,
      image_dimensions: $('Extract Content Type Prompt').item.json.property_image_dimensions,
      image_system_message: $('Extract Content Type Prompt').item.json.property_image_system_message,
      initial_article: $('Get Initial Article').item.json.body.data.url,
    },
  },
];

